// ImageCarousel.jsx
import React, { useState, useEffect, useRef } from 'react';

const ImageCarousel = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const imageRef = useRef(null);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length); // Handle negative index
  };

  useEffect(() => {
    const intervalId = setInterval(() => handleNext(), 5000); // Change interval as needed

    return () => clearInterval(intervalId); // Clear interval on unmount
  }, []);

  return (
    <div className="image-carousel">
      <img ref={imageRef} src={images[currentIndex]} alt="" />
      <button onClick={handlePrev} className="carousel-button prev">&#8592;</button>
      <button onClick={handleNext} className="carousel-button next">&#8594;</button>
    </div>
  );
};

export default ImageCarousel;
