// ImageCarousel.jsx (updated)
import React, { useState, useEffect, useRef } from 'react';

const ImageCarousel = ({ images, transitionTime = 1000 }) => { // Set default transition time
  const [currentIndex, setCurrentIndex] = useState(0);
  const imageRef = useRef(null);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  useEffect(() => {
    const intervalId = setInterval(() => handleNext(), transitionTime); // Set interval based on transition time

    return () => clearInterval(intervalId); // Clear interval on unmount
  }, [transitionTime, images]); // Update interval on transitionTime or images change

  const imageTransitionStyle = {
    transform: `translateX(-${currentIndex * 100}%)`, // Translate images based on currentIndex
    transition: `transform ${transitionTime}ms ease-in-out`, // Smooth transition animation
  };

  return (
    <div className="image-carousel">
      <div ref={imageRef} className="image-container" style={imageTransitionStyle}>
        {images.map((image, index) => (
          <img key={index} src={image} alt="" className="carousel-image" />
        ))}
      </div>
      <button onClick={handlePrev} className="carousel-button prev">&#8592;</button>
      <button onClick={handleNext} className="carousel-button next">&#8594;</button>
    </div>
  );
};

export default ImageCarousel;
