import React, { useState } from 'react'; // Import useState hook

const Project = () => {
  const { imageUrl, title, description, link } = projectData; // Destructuring

  const [isLoading, setIsLoading] = useState(true); // Track image loading state
  const [error, setError] = useState(null); // Track image loading error

  const handleImageLoad = () => setIsLoading(false);
  const handleImageError = (error) => {
    setError(error);
    setIsLoading(false); // Set loading state to false even on error
  };

  const backgroundImageStyle = {
    backgroundImage: `url(${imageUrl})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    width: '100%',
    height: '200px', // Adjust as needed
  };

  return (
    <div className="project-card">
      <div className="project-image-container">
        {isLoading && <div className="loading">Loading...</div>}
        {error && <div className="error">Error loading image</div>}
        <img
          src={imageUrl}
          alt={title}
          onLoad={handleImageLoad}
          onError={handleImageError}
          style={backgroundImageStyle}
        />
      </div>
      <div className="project-info">
        <h3>{title}</h3>
        <p>{description}</p>
        <a href={link} target="_blank" rel="noopener noreferrer">
          View Project
        </a>
      </div>
    </div>
  );
};

export default Project;
