import React from 'react'; // Import React

function Project(projectData) {
  const { imageUrl, title, description, link } = projectData; // Destructuring

  const [isLoading, setIsLoading] = useState(true); // Track image loading state
  const [error, setError] = useState(null); // Track image loading error

  const handleImageLoad = () => setIsLoading(false);
  const handleImageError = (error) => {
    setError(error);
    setIsLoading(false); // Set loading state to false even on error
  };

  return (
    <div className="project">
      <div className="image-container"> {/* Uses class from project.css */}
        {isLoading && <div className="image-loading">Loading...</div>}
        {error && <div className="image-error">Error: Image failed to load.</div>}
      </div>
      <h3>{title}</h3>
      <p>{description}</p>
      {/* Add more elements based on your project data */}
      <a href={link} target="_blank" rel="noopener noreferrer">
        View Project
      </a>
    </div>
  );
}

export default Project;
